import './Student.css'

class Student extends Component{
    constructor(props){
        super(props);
    }

shouldComponentUpdate(nextProps,nextState){
        console.log('next',nextProps,this.props)
        if(nextProps.content !== this.props.content){
            debugger
            return true
        }
        else{
            return false;
        }
}

    updateInputValue = (event)=>{
        console.log(event.target.value)
    }

    handleSubmit = (event)=>{
        event.preventDefault();
        this.props.onSearchTermSubmit(this.textInput.value);
    }

 render(){
console.log('student component', this.props.content.Name);
     return(
         <div className='student-container'>
             <div>
                 Index: {this.props.content.ID}
             </div>
             <div>Name: {this.props.content.Name}</div>
             <button onClick={this.props.deleteStudent.bind(this,this.props.index)}>delete</button>
             <form onSubmit={this.handleSubmit}>
             <input type="text"  ref={(input) => this.textInput = input} />
             </form>
         </div>
     )
    }
}

export default Student;
